// تأثير الإضاءة عند الحركة
document.addEventListener('DOMContentLoaded', () => {
  // إنشاء عنصر الإضاءة الديناميكي
  const glow = document.createElement('div');
  glow.className = 'glow-effect';
  document.body.appendChild(glow);

  // إعدادات الإضاءة
  let mouseX = 0, mouseY = 0;
  let touchX = 0, touchY = 0;

  // تحديث موقع الإضاءة بناءً على الحركة
  const updateGlowPosition = (x, y) => {
    glow.style.left = x + 'px';
    glow.style.top = y + 'px';
    glow.style.opacity = '1';
    setTimeout(() => glow.style.opacity = '0', 100);
  };

  // استجابة لحركة الماوس
  document.addEventListener('mousemove', (e) => {
    mouseX = e.clientX;
    mouseY = e.clientY;
    updateGlowPosition(mouseX, mouseY);
  });

  // استجابة للّمس على الشاشات اللوحية
  document.addEventListener('touchmove', (e) => {
    e.preventDefault();
    touchX = e.touches[0].clientX;
    touchY = e.touches[0].clientY;
    updateGlowPosition(touchX, touchY);
  });

  // تأثير اهتزاز العناصر عند التمرير
  const animateOnScroll = () => {
    document.querySelectorAll('[data-aos]').forEach(el => {
      const rect = el.getBoundingClientRect();
      if (rect.top < window.innerHeight * 0.8) {
        el.style.transform = `translateY(${Math.sin(rect.top * 0.1) * 10}px)`;
      }
    });
    requestAnimationFrame(animateOnScroll);
  };
  animateOnScroll();
});
